let iconMenuMain = document.querySelector(".header__menu-burger-toogle");
let iconMenuMainClose = document.querySelector('.burger-menu__close-btn');
if (iconMenuMain != null) {
	console.log('Урааа');
	let menuBody = document.querySelector(".burger-menu");
	iconMenuMain.addEventListener("click", function (e) {
		if (!menuBody.classList.contains('burger-menu_opened')) {
			menuBody.classList.toggle("burger-menu_opened");
		}
	});
};
if (iconMenuMainClose != null) {
	console.log('Урааа');
	let menuBody = document.querySelector(".burger-menu");
	iconMenuMainClose.addEventListener("click", function (e) {
		if (menuBody.classList.contains('burger-menu_opened')) {
			menuBody.classList.remove("burger-menu_opened");
		}
	});
};

function filterFormMobile() {
	document.querySelector('.form-bg-text').addEventListener('click', function(){		
		document.querySelector('.form-bg').classList.toggle('form--hide');
		document.querySelector('.form__fieldset').classList.toggle('form--hide');
	});
}
filterFormMobile();